import { Line } from 'vue-chartjs';
import 'chart.js';
export default {
  components: {
    LineChart: {
      extends: Line,
      props: ['chartData'],
      mounted() {
        this.renderChart(this.chartData, {
          responsive: true,
          maintainAspectRatio: false
        });
      }
    }
  },
  data() {
    return {
      selectedPeriod: 'Monthly',
      activeMetric: 'stress',
      chartData: {
        labels: ['September', 'November', 'December', 'January'],
        datasets: [{
          label: 'Stress level',
          backgroundColor: 'rgba(54, 162, 235, 0.2)',
          borderColor: '#36A2EB',
          data: [2, 4, 3, 6, 4],
          fill: true
        }]
      }
    };
  },
  watch: {
    activeMetric(newMetric) {
      this.updateChartData(newMetric);
    }
  },
  methods: {
    updateChartData(metric) {
      const dataMap = {
        stress: [2, 4, 3, 6, 4],
        pulse: [75, 80, 78, 85, 77],
        temperature: [36.5, 36.6, 37, 36.8, 36.7],
        calories: [200, 210, 220, 230, 240]
      };
      this.chartData.datasets[0].data = dataMap[metric];
      this.chartData.datasets[0].label = this.getLabel(metric);
      this.$refs.chart.update();
    },
    getLabel(metric) {
      const labels = {
        stress: 'Stress level',
        pulse: 'Pulse',
        temperature: 'Temperature',
        calories: 'Calories burned'
      };
      return labels[metric];
    }
  }
};
